import React from 'react'
import { Container, Row } from 'react-bootstrap'
import Issue from '../../model/issue/Issue'
import { getIssues } from '../../service/getIssues'
import './issues.scss'

export default class Issues extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      arr: []
    }
  }

  componentDidMount = props => {
    this.setState({
      arr: this.getIssues()
    })
  }

  // componentDidUpdate = props => {
  //   this.setState({
  //     arr: this.getIssues
  //   })
  // }
  getIssues = () => {
    return getIssues().then(res => console.log(res.data.conversations))
  }

  render () {
    return (
      <Container>
        <Row>
          <div className='issue-cards col-6'>
            {this.state.arr.map(issue => (
              <Issue
                key={issue.id}
                id={issue.id}
                title={issue.title}
                description={issue.description}
                labels={issue.labels}
              />
            ))}
          </div>
        </Row>
      </Container>
    )
  }
}
