import React, { useState } from 'react'
import { Card, Container, Form, Row, Dropdown } from 'react-bootstrap'

import './scss/issues.scss'

export default function Issues () {
  const CustomToggle = React.forwardRef(({ children, onClick }, ref) => (
    <a
      href='www'
      ref={ref}
      onClick={e => {
        e.preventDefault()
        onClick(e)
      }}
    >
      {children}
      &#x25bc;
    </a>
  ))

  const CustomMenu = React.forwardRef(
    ({ children, style, className, 'aria-labelledby': labeledBy }, ref) => {
      const [value, setValue] = useState('')

      return (
        <div
          ref={ref}
          style={style}
          className={className}
          aria-labelledby={labeledBy}
        >
          <Form.Control
            autoFocus
            className='mx-3 my-2 w-auto'
            placeholder='Type to filter...'
            onChange={e => setValue(e.target.value)}
            value={value}
          />
          <ul className='list-unstyled'>
            {React.Children.toArray(children).filter(
              child =>
                (!value || child.props.children.startsWith(value)) || (!value || child.props.children.toLowerCase().startsWith(value))
            )}
          </ul>
        </div>
      )
    }
  )

  return (
    <Container className='issues-container'>
      <Row>
        <Container>
          <Card>
            <Card.Body>
              <Form className="check-all">
                <Form.Check></Form.Check>
              </Form>
              <Dropdown className="dropdown-menu">
                <Dropdown.Toggle
                  as={CustomToggle}
                  id='dropdown-custom-components'
                >
                  Label
                </Dropdown.Toggle>

                <Dropdown.Menu as={CustomMenu}>
                  <Dropdown.Item eventKey='1'>Red</Dropdown.Item>
                  <Dropdown.Item eventKey='2'>Blue</Dropdown.Item>
                  <Dropdown.Item eventKey='3' active>
                    Orange
                  </Dropdown.Item>
                  <Dropdown.Item eventKey='1'>Red-Orange</Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </Card.Body>
          </Card>
        </Container>
      </Row>
    </Container>
  )
}
