import React from 'react'
import { createIssue } from '../../service/createIssue'
import './add-issue.scss'
import { Button, Modal, Container, Form } from 'react-bootstrap'

export default class AddIssue extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      title: '',
      description: '',
      labels: [],
      lgShow: false
    }

    // this.handleTitle = this.handleTitle.bind(this)
    // this.handleDescription = this.handleDescription.bind(this)
    this.setLgShow = this.setLgShow.bind(this)
  }

  // handleTitle = event => {
  //   this.setState({
  //     title: event.target.value
  //   })
  // }

  // handleDescription = event => {
  //   this.setState({
  //     description: event.target.value
  //   })
  // }

  // handleSubmit = event => {
  //   const response = addIssue(this.state.title, this.state.description, null)
  //   event.preventDefault()
  //   console.log(response)
  // }

  // addIssue = () => {
  //   createIssue()
  // }

  setLgShow (lgShow) {
    this.setState({
      lgShow: lgShow
    })
  }

  render () {
    return (
      <Container>
        <Button
          variant=''
          className='add-issue-btn'
          onClick={() => {
            this.setLgShow(true)
          }}
          // onClick={()=>{
          //   createIssue("", "", [])
          // }}
        >
          Add A New Issue
        </Button>
        <Modal
          size='lg'
          show={this.state.lgShow}
          onHide={() => {
            this.setLgShow(false)
          }}
          aria-labelledby='example-modal-sizes-title-lg'
        >
          <Modal.Header closeButton>
            <Modal.Title id='example-modal-sizes-title-lg'>
              Add A New Issue
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId="newIssue">
                <Form.Label>Title</Form.Label>
                <Form.Control type="text" placeholder="Ex. Issue tracker 1"></Form.Control>
              </Form.Group>
            </Form>
          </Modal.Body>
        </Modal>
      </Container>
    )
  }
}
