import axios from 'axios'
import Issues from '../component/issue/Issues'

export const createIssue = async (title, description, labels) => {
  const url = 'http://localhost:8080/issue'

  let issue = {
    title: title,
    description: description,
    labels: []
  }

  labels.forEach(element => {
    issue.labels.push({ labelName: element.substring(1) })
  })

  const response = await axios.post(url, issue, {
    headers: { 'content-type': 'application/json' }
  })

  const update = () => {
    new Issues().componentDidMount()
  }
  update()
  return response
}
