import React from 'react'
import axios from 'axios'
import { Issue } from '../component/issue'

export default class Issues extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      arr:[]
    }
    this.getIssueList = this.getIssueList.bind(this)
  }

  componentDidMount () {
    window.addEventListener('load', this.getIssueList)
  }

  getIssueList () {
    axios
      .get(
        'https://issue-tracker-spring-boot.herokuapp.com/issues'
      )
      .then(issues => {
        this.setState({
          arr: issues.data
        })
      })
      .catch(error => {
        console.log(error)
      })
  }

  render () {
    return (
      <article className="row">
          {this.state.arr.map((issue) => (
            <Issue
              key={issue.id}
              id={issue.id}
              title={issue.title}
              description={issue.description}
            />
          ))}
      </article>
    )
  }
}
