import React from 'react'
import { Issue } from '../../model/issue/Issue'
import { getIssues } from '../../service/getIssues'
import { Container, Row } from 'react-bootstrap'

export default class Issues extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      arr: []
    }
  }

  componentDidMount = () => {
    getIssues().then(response => {
      this.setState({
        arr: response.data
      })
    })
  }

  componentDidUpdate = () => {
    getIssues().then(response => {
      this.setState({
        arr: response.data
      })
    })
    this.shouldComponentUpdate(false)
  }

  shouldComponentUpdate=(shouldUpdate)=> {
    return shouldUpdate
  }

  render () {
    return (
      <Container>
        <Row className='row'>
          {this.state.arr.map(issue => (
            <Issue
              key={issue.id}
              id={issue.id}
              title={issue.title}
              description={issue.description}
            />
          ))}
        </Row>
      </Container>
    )
  }
}
