import React from 'react'
import { addIssue } from '../service/addIssue'

export default class AddIssue extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      title: '',
      description: '',
      labels: []
    }

    this.handleTitle = this.handleTitle.bind(this)
    this.handleDescription = this.handleDescription.bind(this)
  }

  handleTitle = event => {
    this.setState({
      title: event.target.value
    })
  }

  handleDescription = event => {
    this.setState({
      description: event.target.value
    })
  }

  handleSubmit = event => {
    const response = addIssue(this.state.title, this.state.description, null)
    event.preventDefault()
    console.log(response)
  }

  render () {
    return (
      <div className='container'>
        <div className='row'>
          <div className='d-grid gap-2'>
            <button className='btn' type='button' data-bs-toggle="modal" data-bs-target="#exampleModal">
              Add A New Issue
            </button>
          </div>
        </div>
      </div>
    )
  }
}
