import React from 'react'
import './issue.scss'
import Issues from '../service/getIssues'

export class Issue extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      selectedIssues: []
    }
    this.addIssues = this.addIssues.bind(this)
  }

  addIssues (issueId, checkValue) {
    const oldSelectedIssues = this.state.selectedIssues
    console.log(oldSelectedIssues)
    oldSelectedIssues.push(issueId)

    this.setState({
      selectedIssues: oldSelectedIssues
    })

    console.log(checkValue)
    console.log(issueId)
  }

  render () {
    const { id, title, description } = this.props

    return (
      <div className='card'>
        <div className='card-body'>
          <div className='edit-container'>
            <button type='button' className='btn btn-sm btn-info'>
              <i className='edit-icon'> </i>{' '}
            </button>{' '}
            <button type='button' className='btn btn-sm btn-danger'>
              <i className='delete-icon'> </i>{' '}
            </button>{' '}
            <div className='select'>
              <input
                onChange={e => {
                  this.addIssues(id, e.target.checked)
                }}
                type='checkbox'
                className='btn-check'
                id='btncheck1'
                autoComplete='off'
              />
            </div>{' '}
          </div>{' '}
          <h5 className='card-title'> {title} </h5>{' '}
          <p className='card-text'> {description} </p>{' '}
          <button className='btn btn-primary'> {id} </button>{' '}
        </div>{' '}
      </div>
    )
  }
}
