import axios from 'axios'

export const editIssue = async (id, title, description, labels) => {
  const url = 'http://localhost:8080/issue/' + id

  let issue = {
    title: title,
    description: description,
    labels: []
  }

  console.log(labels)

  labels.forEach(element => {

    issue.labels.push({ labelName: element })
  })

  const response = await axios.put(url, issue, {
    headers: { 'content-type': 'application/json' }
  }).catch(error => (
    console.log(error)
  ))
  //window.location.reload()
  return response
}
