import React from 'react'
import { Issue } from '../../model/issue/Issue'
import { getIssues } from '../../service/getIssues'
import { Container, Row } from 'react-bootstrap'

export default class Issues extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      arr: []
    }
  }

  componentDidMount = () => {
    getIssues().then(response => {
      this.setState({
        arr: response.data
      })
    })
  }

  shouldComponentUpdate = () => {
    return true
  }

  componentDidUpdate = () => {
    getIssues().then(response => {
      if (response.data !== this.state.arr) {
        // this.setState({
        //   arr: response.data
        // })
      } else {
        return null
      }
    })
  }

  render () {
    return (
      <Container>
        <Row>
          <div class='issue-cards col-6'>
            {this.state.arr.map(issue => (
              <Issue
                key={issue.id}
                id={issue.id}
                title={issue.title}
                description={issue.description}
              />
            ))}
          </div>
        </Row>
      </Container>
    )
  }
}
