import React, { useEffect, useState } from 'react'
import { Container, Form, Navbar, Nav, Dropdown } from 'react-bootstrap'
import { getLabels } from '../../service/getLabels'

import './scss/issues.scss'

export default function Issues () {
  // var _ = require('lodash')
  let [labels, setItem] = useState([])

  useEffect(() => {
    getLabels()
  }, [labels])

  return (
    <Container className='issue-container'>
      <Navbar collapseOnSelect expand='lg' bg='dark' variant='dark'>
        <Form>
          <Form.Check></Form.Check>
        </Form>
        <Navbar.Toggle aria-controls='responsive-navbar-nav' />
        <Navbar.Collapse id='responsive-navbar-nav'>
          <Nav className='mr-auto'></Nav>
          <Nav>
            <Dropdown>
              <Dropdown.Toggle id='dropdown-basic' variant='outline-success'>
                Label
              </Dropdown.Toggle>
              <Dropdown.Menu className='dropdown-container'>
                <Form>
                  <Form.Control
                    type='text'
                    placeholder='type something..'
                  ></Form.Control>
                </Form>
                {labels.map(label => {
                  return (
                    <Dropdown.Item
                      href=''
                      key={label.id}
                      style={{ background: label.labelColor }}
                    >
                      {label.labelName}
                    </Dropdown.Item>
                  )
                })}
              </Dropdown.Menu>
            </Dropdown>
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    </Container>
  )
}
