import React from 'react'
import { createIssue } from '../../service/createIssue'
import './add-issue.scss'
import {
  Button,
  Modal,
  Container,
  Form,
  InputGroup,
  FormControl
} from 'react-bootstrap'

export default class AddIssue extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      title: '',
      description: '',
      labels: [],
      label: '',
      lgShow: false
      // displayColorPicker: false,
      // defaultColor: '#222',
      // changeColor: '',
      // color: {
      //   r: '34',
      //   g: '34',
      //   b: '34',
      //   a: '1'
      // }
    }

    this.setLgShow = this.setLgShow.bind(this)
  }

  handleFormValues = event => {
    event.preventDefault()
    const title = event.target.title.value
    const description = event.target.description.value
    console.log(title)
    console.log(description)
    console.log(this.state.labels)
  }
  handleLabel = event => {
    event.preventDefault()
    this.setState({ labels: this.state.label })

  }
  handleChangeLabel = event => {
    this.setState({ label: <Badge>{event.target.value}</Badge> })
  }
  addIssue = () => {
    createIssue()
  }

  setLgShow (lgShow) {
    this.setState({
      lgShow: lgShow
    })
  }

  render () {
    return (
      <Container>
        <Button
          variant=''
          className='add-issue-btn'
          onClick={() => {
            this.setLgShow(true)
          }}
        >
          Add A New Issue
        </Button>
        <Modal
          size='lg'
          show={this.state.lgShow}
          onHide={() => {
            this.setLgShow(false)
          }}
          aria-labelledby='example-modal-sizes-title-lg'
        >
          <Modal.Header closeButton>
            <Modal.Title id='example-modal-sizes-title-lg'>
              Add A New Issue
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form onSubmit={this.handleFormValues}>
              <Form.Group controlId='newIssue'>
                <Form.Label>Title : </Form.Label>
                <Form.Control
                  type='text'
                  name='title'
                  placeholder="Ex. 'Issue tracker 1' "
                ></Form.Control>
                <Form.Label className='description-label'>
                  Description :{' '}
                </Form.Label>
                <Form.Control
                  className='description-text-area'
                  as='textarea'
                  name='description'
                  rows={3}
                  placeholder="Ex. 'Issue tracker 1 Description' "
                ></Form.Control>
                <Form className='add-label'>
                  <Form.Label>Labels : </Form.Label>
                  <InputGroup className='mb-3'>
                    <InputGroup.Prepend>
                      <InputGroup.Text>#</InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      aria-label='label'
                      type='text'
                      name='label'
                      placeholder='Bug, Front-End, ... '
                      value={this.state.label}
                      onChange={this.handleChangeLabel}
                    />
                    <InputGroup.Append>
                      <Button
                        id='add-label-btn'
                        className='add-label-btn'
                        variant='success'
                        type='button'
                        onClick={this.handleLabel}
                      >
                        Add
                      </Button>
                    </InputGroup.Append>
                  </InputGroup>
                </Form>
                <Container className='create-btn-group'>
                  <Button
                    id='create-btn'
                    className='create-btn'
                    variant='primary'
                    type='submit'
                    block
                  >
                    Create
                  </Button>
                  <Button
                    id='cancel-btn'
                    className='cancel-btn'
                    variant='danger'
                    type='cancel'
                    block
                  >
                    Cancel
                  </Button>
                </Container>
              </Form.Group>
            </Form>
          </Modal.Body>
        </Modal>
      </Container>
    )
  }
}
