import React from 'react'
import { Container, Row } from 'react-bootstrap'
import Issue from '../../model/issue/Issue'
import { getIssues } from '../../service/getIssues'
import './issues.scss'

export default class Issues extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      arr: []
    }
  }

  componentDidMount = props => {
    const issues = getIssues().then(res => res.data)
    this.setState({
      arr: [...res.data]
    })
  }

  // componentDidUpdate = props => {
  //   const issues = getIssues().then(res => res.data)
  //   if (issues !== this.state.arr) {
  //     this.setState({
  //       arr: issues
  //     })
  //   }
  // }

  getIssues = () => {
    return Array.from(this.state.arr)
  }

  render () {
    return (
      <Container>
        <Row>
          <div className='issue-cards col-6'>
            {this.getIssues().map(issue => (
              <Issue
                key={issue.id}
                id={issue.id}
                title={issue.title}
                description={issue.description}
                labels={issue.labels}
              />
            ))}
          </div>
        </Row>
      </Container>
    )
  }
}
