import React from 'react'
import { createIssue } from '../../service/createIssue'
import './add-issue.scss'
import {
  Button,
  Modal,
  Container,
  Form,
  InputGroup,
  FormControl
} from 'react-bootstrap'

export default class AddIssue extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      title: '',
      description: '',
      labels: [],
      lgShow: false,
      displayColorPicker: false,
      defaultColor: '#222',
      changeColor: '',
      color: {
        r: '34',
        g: '34',
        b: '34',
        a: '1'
      }
    }

    // this.handleTitle = this.handleTitle.bind(this)
    // this.handleDescription = this.handleDescription.bind(this)
    this.setLgShow = this.setLgShow.bind(this)
  }

  // handleTitle = event => {
  //   this.setState({
  //     title: event.target.value
  //   })
  // }

  // handleDescription = event => {
  //   this.setState({
  //     description: event.target.value
  //   })
  // }

  // handleSubmit = event => {
  //   const response = addIssue(this.state.title, this.state.description, null)
  //   event.preventDefault()
  //   console.log(response)
  // }

  // addIssue = () => {
  //   createIssue()
  // }

  setLgShow (lgShow) {
    this.setState({
      lgShow: lgShow
    })
  }

  onHandleShowColorPicker = () => {
    this.setState({ displayColorPicker: true })
  }

  render () {
    return (
      <Container>
        <Button
          variant=''
          className='add-issue-btn'
          onClick={() => {
            this.setLgShow(true)
          }}
          // onClick={()=>{
          //   createIssue("", "", [])

          // }}
        >
          Add A New Issue
        </Button>
        <Modal
          size='lg'
          show={this.state.lgShow}
          onHide={() => {
            this.setLgShow(false)
          }}
          aria-labelledby='example-modal-sizes-title-lg'
        >
          <Modal.Header closeButton>
            <Modal.Title id='example-modal-sizes-title-lg'>
              Add A New Issue
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Form>
              <Form.Group controlId='newIssue'>
                <Form.Label>Title : </Form.Label>
                <Form.Control
                  type='text'
                  placeholder="Ex. 'Issue tracker 1' "
                ></Form.Control>
                <Form.Label className='description-label'>
                  Description :{' '}
                </Form.Label>
                <Form.Control
                  className='description-text-area'
                  as='textarea'
                  rows={3}
                  placeholder="Ex. 'Issue tracker 1 Description' "
                ></Form.Control>
                <Form.Group className='add-label'>
                  <Form.Label>Labels : </Form.Label>
                  <InputGroup className='mb-3'>
                    <InputGroup.Prepend>
                      <InputGroup.Text>#</InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl
                      aria-label='label'
                      type='text'
                      placeholder="Ex. 'Issue tracker 1' "
                    >
                      {' '}
                      {/* Color picker */}
                    </FormControl>
                    <InputGroup.Append>
                      <Button>Add</Button>
                    </InputGroup.Append>
                  </InputGroup>
                </Form.Group>
                <Container className='create-btn-group'>
                  <Button
                    id='create-btn'
                    className='create-btn'
                    variant='primary'
                    type='submit'
                    block
                  >
                    Create
                  </Button>
                  <Button
                    id='cancel-btn'
                    className='cancel-btn'
                    variant='danger'
                    type='cancel'
                    block
                  >
                    Cancel
                  </Button>
                </Container>
              </Form.Group>
            </Form>
          </Modal.Body>
        </Modal>
      </Container>
    )
  }
}
