import React from 'react'
import { Issue } from '../../model/issue/Issue'
import { getIssues } from '../../service/getIssues'

export default class Issues extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      arr: []
    }
  }
  componentDidMount () {
    getIssues.then(response => response.json())
        .then(data => this.setState({ arr: data }));
  }

  render () {
    return (
      <article className='container'>
        <div className='row'>
          {this.state.arr.map(issue => (
            <Issue
              key={issue.id}
              id={issue.id}
              title={issue.title}
              description={issue.description}
            />
          ))}
        </div>
      </article>
    )
  }
}
