import React from 'react'
import { createIssue } from '../../service/createIssue'
import './add-issue.scss'
import Issues from './Issues'

export default class AddIssue extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      title: '',
      description: '',
      labels: []
    }

    this.handleTitle = this.handleTitle.bind(this)
    this.handleDescription = this.handleDescription.bind(this)
  }

  handleTitle = event => {
    this.setState({
      title: event.target.value
    })
  }

  handleDescription = event => {
    this.setState({
      description: event.target.value
    })
  }

  // handleSubmit = event => {
  //   const response = addIssue(this.state.title, this.state.description, null)
  //   event.preventDefault()
  //   console.log(response)
  // }

  addIssue = () => {
    createIssue();
  }

  render () {
    return (
      <div className='container'>
        <button type='button' className='btn btn-lg btn-block add-issue-btn' onClick={this.addIssue}>
          Add A New Issue
        </button>
      </div>
    )
  }
}
