import './issue.scss'
import { deleteIssue } from '../../service/deleteIssue'
import { Card, Button, Form, Col } from 'react-bootstrap'

export default function Issue({props}) {
  const [arr, setArr] = useState([])

  const { id, title, description, labels } = this.props

  useEffect(() => {
    getIssues().then(data => setArr(data.data))
  }, [props, this.state])

  return (
    <Col sm={{ span: 12 }}>
      <Card style={{ width: '30rem' }} className='card'>
        <Card.Body>
          <div className='edit-container'>
            <Button type='button' size='sm'>
              <i className='edit-icon'> </i>{' '}
            </Button>{' '}
            <Button variant='danger' type='button' size='sm'>
              <i
                className='delete-icon'
                onClick={() => {
                  deleteIssue(id)
                }}
              >
                {' '}
              </i>{' '}
            </Button>{' '}
            <Form.Check type='checkbox' id='btncheck1' autoComplete='off' />
          </div>{' '}
          <Card.Title> {title} </Card.Title>{' '}
          <Card.Text> {description} </Card.Text>
        </Card.Body>{' '}
      </Card>
    </Col>
  )
}
