import axios from 'axios'
import { withRouter } from 'react-router'

export const addIssue = (issueTitle, issueDescription, issueLabels) => {
  const url = 'https://issue-tracker-spring-boot.herokuapp.com/issue'
  const issue = {
    title: issueTitle,
    description: issueDescription,
    labels: [{ labelName: 'deneme label' }]
  }
  const { router } = this.props

  axios.post(url, issue).then(response => router.push('/'))
}
