import React from 'react'
import './issue.scss'
import { deleteIssue } from '../../service/deleteIssue'
import { Card, Button, Form, Col, Badge } from 'react-bootstrap'
import { getIssue } from '../../service/getIssue'

export default class Issue extends React.Component {
  showDetailedIssue = id => {
    let issue = getIssue(id).then(res => res.data)
    // this.setState({ selectedIssue: issue })
  }
  render () {
    const { id, title, description, labels } = this.props
    return (
      <Col sm={{ span: 12 }}>
        <Card
          style={{ width: '30rem' }}
          className='card'
          onClick={() => {
            this.showDetailedIssue(id)
          }}
        >
          <Card.Body>
            <div className='edit-container'>
              <Button type='button' size='sm'>
                <i className='edit-icon'> </i>{' '}
              </Button>{' '}
              <Button variant='danger' type='button' size='sm'>
                <i
                  className='delete-icon'
                  onClick={() => {
                    deleteIssue(id)
                  }}
                >
                  {' '}
                </i>{' '}
              </Button>{' '}
              <Form.Check type='checkbox' id='btncheck1' autoComplete='off' />
            </div>{' '}
            <Card.Title> {title} </Card.Title>{' '}
            <Card.Text> {description} </Card.Text>
            {labels.map(item => (
              <Badge className='label' key={item.id} variant='primary'>
                #{item.labelName}
              </Badge>
            ))}
          </Card.Body>{' '}
        </Card>
      </Col>
    )
  }
}
