import axios from 'axios'

export const editIssue = async (id, title, description, labels) => {
  const url = 'http://localhost:8080/issue/' + id

  let issue = {
    title: title,
    description: description,
    labels: []
  }

  labels.forEach(element => {
    console.log(element)
    issue.labels.push({ labelName: element.substring(1) })
  })

  const response = await axios.put(url, issue, {
    headers: { 'content-type': 'application/json' }
  })
  window.location.reload()
  return response
}
