import axios from 'axios'

export const deleteIssue = async issueId => {
  const url = 'http://localhost:8080/issue/' + issueId

  const response = await axios.delete(url)

  const refreshPage = () => {
    window.location.reload()
  }
  refreshPage()

  return response
}
