import React from 'react'
import { addIssue } from '../service/addIssue'

export default class AddIssue extends React.Component {
  constructor (props) {
    super(props)
    this.state = {
      title:null,
      description:null
    }
  }
  addIssue () {
    console.log(this.state.issue)
  }

  handleTitle = event => {
    this.setState({
      title: event.value
    })
  }

  handleDescription = event => {
    this.setState({
      description : event.value
    })
  }

  render () {
    return (
      <div className='container'>
        <form>
          <div className='mb-3'>
            <label for='title' className='form-label'>
              Title
            </label>
            <input
              type='text'
              className='form-control'
              id='title'
              onChange={this.handleTitle}
            />
          </div>
          <div className='mb-3'>
            <label for='description' className='form-label'>
              Description
            </label>
            <div>
              <textarea
                id='description'
                name='description'
                cols='100'
                rows='5'
                onChange={this.handleDescription}
              ></textarea>
            </div>
          </div>
          <button
            type='button'
            className='btn btn-primary'
            onClick={this.addIssue}
          >
            Submit
          </button>
        </form>
      </div>
    )
  }
}
